import React, { Component } from 'react';
//import SimpleSlider from './slider';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/lab/Slider';

const styles = {
    root: {
      width: 300,
    },
    slider: {
      padding: '22px 0px',
    },
};

class App extends React.Component {
    state = {
        loan_value: 500,
        month_value: 6,
    };

    handleLoanChange = (event, loan_value) => {
        this.setState({loan_value});
        let url = 'https://ftl-frontend-test.herokuapp.com/interest?amount='+loan_value+'&numMonths='+this.state.month_value;
        console.log('url', url)
        fetch(url)
        .then(results => {
            return results.json();
        })
        .then(data => {
            let interestRate = data.interestRate;
            let monthlyPaymentAmount = data.monthlyPayment.amount
            let numPayments = data.numPayments
            console.log('interestRate', interestRate)
            console.log('monthlyPaymentAmount', monthlyPaymentAmount)
            console.log('numPayments', numPayments)
        });

    }

    handleMonthChange = (event, month_value) => {
        this.setState({month_value})
        let url = 'https://ftl-frontend-test.herokuapp.com/interest?amount='+this.state.loan_value+'&numMonths='+month_value;
        console.log('url', url)
        fetch(url)
        .then(results => {
            return results.json();
        })
        .then(data => {
            let interestRate = data.interestRate;
            let monthlyPaymentAmount = data.monthlyPayment.amount
            let numPayments = data.numPayments
            console.log('interestRate', interestRate)
            console.log('monthlyPaymentAmount', monthlyPaymentAmount)
            console.log('numPayments', numPayments)
        });
    }

    render() {
        const {classes} = this.props;
        return (
        <div style = {{width: 300}}>
            <Typography id="label">Loan {this.state.loan_value}</Typography>
            <Slider
            classes={{ container: classes.slider }}
            value={this.state.loan_value}
            min = {500}
            max = {5000}
            step = {1}
            aria-labelledby="label"
            onChange={this.handleLoanChange}
            />

            <Typography id="label">Month {this.state.month_value}</Typography>
            <Slider
            classes={{ container: classes.slider }}
            value={this.state.month_value}
            min = {6}
            max = {24}
            step = {1}
            aria-labelledby="label"
            onChange={this.handleMonthChange}
            />
        </div>    
        );
    }
}

App.propTypes = {
    classes: PropTypes.object.isRequired,
    onChange: PropTypes.func,
  };

export default withStyles(styles)(App);
