import React, { Component } from 'react';
//import SimpleSlider from './slider';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/lab/Slider';

const styles = {
    root: {
      width: 300,
    },
    slider: {
      padding: '22px 0px',
    },
};

export default class App extends React.Component {
    state = {
        loan_value: 500,
        month_value: 6,
    };

    handleLoanChange = (event, loan_value) => {
        console.log("event_loan", event);
        console.log("value_loan", loan_value);
        this.setState({loan_value});
    }

    handleMonthChange = (event, month_value) => {
        console.log("event_month", event);
        console.log("value_month", month_value);
        this.setState({month_value})
    }

    render() {
        const {classes} = this.props;
        console.log('classes', classes)
        return (
        <div>
            <Typography id="label">Loan {this.state.loan_value}</Typography>
            <Slider
            style = {this.styles}
            classes={{ container: classes.slider }}
            value={this.state.loan_value}
            min = {this.props.min}
            max = {this.props.max}
            step = {this.props.step}
            aria-labelledby="label"
            onChange={this.handleLoanChange}
            />
        </div>
        );
    }
}
